INSERT INTO massages (id, name, short_description, long_description, duration, media_type, media_url, purpose_tags, sessions, is_featured, is_campaign, layout_template, sort_order, created_at, updated_at) VALUES ('74509d06-de2b-4a08-9e2e-18ce717003ca', 'İsveç Masajı', 'Klasik terapötik masaj teknikleri', 'Dünyaca ünlü İsveç masajı, kaslarınızdaki gerginliği çözmek için tasarlanmış klasik bir masaj tekniğidir. Uzun, akıcı hareketler ve derin baskı teknikleriyle kan dolaşımınızı hızlandırır, kas ağrılarını azaltır ve genel vücut rahatlığı sağlar. Spor sonrası toparlanma için idealdir.', '30-90 dakika', 'photo', NULL, '["Kas Gevşetme","Kan Dolaşımı","Ağrı Azaltma"]', '[{"name":"30 Dakika Masaj","price":750},{"name":"30 Dakika Masaj + Kese + Köpük","price":800},{"name":"40 Dakika Masaj + Köpük","price":850},{"name":"40 Dakika Masaj + Kese + Köpük","price":900},{"name":"60 Dakika Masaj + Köpük","price":1000},{"name":"60 Dakika Masaj + Kese + Köpük","price":1100},{"name":"90 Dakika Masaj + Köpük","price":1750},{"name":"90 Dakika Masaj + Kese + Köpük","price":1800}]', 1, 0, 'price-list', 2, '2025-11-23 14:13:25', '2025-11-23 14:13:25');
INSERT INTO massages (id, name, short_description, long_description, duration, media_type, media_url, purpose_tags, sessions, is_featured, is_campaign, layout_template, sort_order, created_at, updated_at) VALUES ('eb8264a8-8d4e-4265-a364-cdcc9c42000e', 'Aromaterapi Masajı', 'Esansiyel yağlarla şifa dolu rahatlama', 'Doğal esansiyel yağların terapötik gücünü masajla birleştiren bu özel uygulama, hem bedeninizi hem de ruhunuzu iyileştirir. Lavanta, okaliptüs, portakal gibi özel yağ karışımları, cildinizden emilerek derin bir rahatlama sağlar. Uyku kalitesini artırır, stresi azaltır ve bağışıklık sistemini güçlendirir.', '30-90 dakika', 'photo', NULL, '["Aromaterapi","Rahatlama","Uyku Kalitesi"]', '[{"name":"30 Dakika Masaj","price":750},{"name":"30 Dakika Masaj + Kese + Köpük","price":800},{"name":"40 Dakika Masaj + Köpük","price":850},{"name":"40 Dakika Masaj + Kese + Köpük","price":900},{"name":"60 Dakika Masaj + Köpük","price":1000},{"name":"60 Dakika Masaj + Kese + Köpük","price":1100},{"name":"90 Dakika Masaj + Köpük","price":1750},{"name":"90 Dakika Masaj + Kese + Köpük","price":1800}]', 1, 0, 'price-list', 3, '2025-11-23 14:13:25', '2025-11-23 14:13:25');
INSERT INTO massages (id, name, short_description, long_description, duration, media_type, media_url, purpose_tags, sessions, is_featured, is_campaign, layout_template, sort_order, created_at, updated_at) VALUES ('541ca34f-dbfa-46d3-9ac4-7fa9a01f02d6', 'Doku Masajı', 'Derin doku terapisi ile kas ağrılarına çözüm', 'Kronik kas ağrıları ve gerginlik için özel olarak geliştirilmiş derin doku masajı, kasların en derin katmanlarına ulaşır. Yavaş ve güçlü hareketlerle kas düğümlerini çözer, sertleşmiş dokuları yumuşatır. Ofis çalışanları, sporcular ve kronik ağrı yaşayanlar için ideal bir terapi yöntemidir.', '30-90 dakika', 'photo', NULL, '["Derin Terapi","Kronik Ağrı","Kas Düğümleri"]', '[{"name":"30 Dakika Masaj","price":750},{"name":"30 Dakika Masaj + Kese + Köpük","price":800},{"name":"40 Dakika Masaj + Köpük","price":850},{"name":"40 Dakika Masaj + Kese + Köpük","price":900},{"name":"60 Dakika Masaj + Köpük","price":1000},{"name":"60 Dakika Masaj + Kese + Köpük","price":1100},{"name":"90 Dakika Masaj + Köpük","price":1750},{"name":"90 Dakika Masaj + Kese + Köpük","price":1800}]', 0, 0, 'price-list', 4, '2025-11-23 14:13:25', '2025-11-23 14:13:25');
INSERT INTO massages (id, name, short_description, long_description, duration, media_type, media_url, purpose_tags, sessions, is_featured, is_campaign, layout_template, sort_order, created_at, updated_at) VALUES ('46e7b38f-29b6-47c1-af60-4176eb8c268d', 'Refleksoloji Masajı', 'Ayak refleks noktalarıyla tüm vücut dengeleme', 'Ayaklarınızdaki refleks noktalarına uygulanan bu özel masaj tekniği, tüm vücudunuzu etkiler. Her refleks noktası vücudunuzun farklı bir organına karşılık gelir. Baskı ve masaj teknikleriyle bu noktalar uyarılarak, iç organlarınızın fonksiyonları desteklenir, enerji akışı düzenlenir ve genel sağlık durumunuz iyileşir.', '30-90 dakika', 'photo', NULL, '["Refleksoloji","Enerji Akışı","Organ Desteği"]', '[{"name":"30 Dakika Masaj","price":750},{"name":"30 Dakika Masaj + Kese + Köpük","price":800},{"name":"40 Dakika Masaj + Köpük","price":850},{"name":"40 Dakika Masaj + Kese + Köpük","price":900},{"name":"60 Dakika Masaj + Köpük","price":1000},{"name":"60 Dakika Masaj + Kese + Köpük","price":1100},{"name":"90 Dakika Masaj + Köpük","price":1750},{"name":"90 Dakika Masaj + Kese + Köpük","price":1800}]', 0, 0, 'price-list', 5, '2025-11-23 14:13:25', '2025-11-23 14:13:25');
INSERT INTO massages (id, name, short_description, long_description, duration, media_type, media_url, purpose_tags, sessions, is_featured, is_campaign, layout_template, sort_order, created_at, updated_at) VALUES ('7b616e87-546d-4043-a0e5-f9f2c04a2555', 'Kese + Köpük', 'Geleneksel Türk hamamı deneyimi', 'Yüzyıllardır süregelen Türk hamam kültürünün vazgeçilmez uygulaması. Özel kese ile cildinizin ölü hücreleri nazikçe temizlenir, gözenekler açılır ve cilt yenilenir. Ardından bol köpüklü sabun masajı ile vücudunuz tertemiz olur ve cildiniz ipek gibi yumuşar. Cildinizi yeniler, kan dolaşımını hızlandırır ve derin bir temizlik sağlar.', '30 dakika', 'photo', NULL, '["Hamam","Cilt Bakımı","Temizlik"]', '[{"name":"Kese + Köpük","price":750}]', 1, 0, 'price-list', 6, '2025-11-23 14:13:25', '2025-11-23 14:13:25');
INSERT INTO massages (id, name, short_description, long_description, duration, media_type, media_url, purpose_tags, sessions, is_featured, is_campaign, layout_template, sort_order, created_at, updated_at) VALUES ('72f7472e-3db1-4655-9111-5bce0d8e91ef', 'Mix Masaj (70 Dakika)', 'Farklı tekniklerin birleşimi ile tam rahatlama', 'En popüler masaj tekniklerinin bir araya geldiği özel bir deneyim. İsveç masajının derin etkisi, aromaterapi yağlarının rahatlığı ve Bali masajının yumuşak dokunuşları bir arada. 70 dakikalık bu özel uygulama, kese ve köpük ile tamamlanarak size tam bir spa deneyimi sunar. Vücudunuzun her bölgesine özel ilgi gösterilir.', '70 dakika', 'photo', NULL, '["Premium","Karma Teknik","Tam Rahatlama"]', '[{"name":"70 Dakika Mix Masaj + Kese + Köpük","price":1600}]', 1, 1, 'price-list', 7, '2025-11-23 14:13:25', '2025-11-23 14:13:25');
INSERT INTO massages (id, name, short_description, long_description, duration, media_type, media_url, purpose_tags, sessions, is_featured, is_campaign, layout_template, sort_order, created_at, updated_at) VALUES ('d87000c4-a3b6-4d19-8ab2-5b329e904ddc', 'Medikal Lokal Masaj', 'Bölgesel ağrı tedavisi ve kas gevşetme', 'Belirli bir bölgedeki ağrı ve gerginliğe odaklanan terapötik masaj. Boyun, sırt, bel veya omuz gibi problem yaşadığınız bölgeye yoğunlaşılır. Medikal masaj teknikleri kullanılarak kas spazmları çözülür, ağrı azaltılır ve hareket kabiliyeti artırılır. Fizik tedavi sonrası destek veya kronik ağrı yönetimi için idealdir.', '30 dakika', 'photo', NULL, '["Medikal","Bölgesel Tedavi","Ağrı Yönetimi"]', '[{"name":"30 Dakika Medikal Lokal","price":1000}]', 0, 0, 'price-list', 8, '2025-11-23 14:13:25', '2025-11-23 14:13:25');
INSERT INTO massages (id, name, short_description, long_description, duration, media_type, media_url, purpose_tags, sessions, is_featured, is_campaign, layout_template, sort_order, created_at, updated_at) VALUES ('65aaf899-0f1a-4467-aaa6-2ed13977ddd8', 'Taş Masajı', 'Sıcak volkanik taşlarla derin rahatlama', 'Volkanik bazalt taşları özel sıcaklıkta ısıtılarak vücudunuzun enerji noktalarına yerleştirilir. Sıcak taşların ısısı kaslarınıza derinlemesine nüfuz eder, gerginliği çözer ve kan dolaşımını hızlandırır. Taşlarla yapılan masaj hareketleri, geleneksel masajın etkisini katlar. Derin bir rahatlama ve huzur hissi sağlar.', '60 dakika', 'photo', NULL, '["Sıcak Taş","Derin Rahatlama","Enerji Noktaları"]', '[{"name":"60 Dakika Taş Masajı","price":1400}]', 1, 0, 'price-list', 9, '2025-11-23 14:13:25', '2025-11-23 14:13:25');
INSERT INTO massages (id, name, short_description, long_description, duration, media_type, media_url, purpose_tags, sessions, is_featured, is_campaign, layout_template, sort_order, created_at, updated_at) VALUES ('4828031a-af9b-4b06-81dc-1242a3a1afbc', 'Medical Masaj Paketi', 'Kapsamlı terapötik masaj ve hamam deneyimi', 'Tıbbi masaj teknikleri ile geleneksel hamam uygulamalarının birleştiği özel bir paket. 50 dakikalık medikal masaj ile kaslarınızdaki tüm gerginlik ve ağrılar giderilir. Ardından kese ve köpük uygulaması ile cildiniz yenilenir. Kronik ağrı, postür bozuklukları veya kas-iskelet sistemi problemleri için ideal bir tedavi programıdır.', '50 dakika', 'photo', NULL, '["Medikal","Terapötik","Kapsamlı Bakım"]', '[{"name":"50 Dakika Medical Masaj + Kese + Köpük","price":1400}]', 0, 0, 'price-list', 10, '2025-11-23 14:13:25', '2025-11-23 14:13:25');
