const input = $input.first().json;
const startTime = Date.now();

// Webhook verification (GET request from Meta)
if (input.query && input.query['hub.mode'] === 'subscribe' && input.query['hub.challenge']) {
  return [{ json: { route: 'verify', challenge: input.query['hub.challenge'] } }];
}

const body = input.body || input;

// Check if it's Instagram webhook
if (body.object !== 'instagram') {
  return [{ json: { route: 'ignore', reason: 'not_instagram' } }];
}

// Parse Instagram messaging webhook
const entry = body.entry?.[0];
if (!entry) {
  return [{ json: { route: 'ignore', reason: 'no_entry' } }];
}

const messaging = entry.messaging?.[0];
if (!messaging) {
  return [{ json: { route: 'ignore', reason: 'no_messaging' } }];
}

// Check for message (not read receipt or other events)
const message = messaging.message;
if (!message || !message.text) {
  return [{ json: { route: 'ignore', reason: 'no_text_message' } }];
}

// IMPORTANT: Ignore echo messages (messages sent by the bot itself)
if (message.is_echo === true) {
  return [{ json: { route: 'ignore', reason: 'echo_message' } }];
}

// Extract sender ID (Instagram-scoped user ID)
const senderId = messaging.sender?.id;
if (!senderId) {
  return [{ json: { route: 'ignore', reason: 'no_sender' } }];
}

return [{ 
  json: { 
    route: 'process', 
    senderId: senderId,
    text: message.text.trim(),
    messageId: message.mid,
    timestamp: messaging.timestamp,
    startTime: startTime
  } 
}];

const parseData = $('Parse Instagram').first().json;
const customerData = $input.first().json;

// Determine customer context
const isNewCustomer = customerData.isNewCustomer !== false;
const interactionCount = customerData.interactionCount || 0;
const couponBalance = customerData.couponBalance || 0;
const totalEarned = customerData.totalEarned || 0;

// Classify intent based on message
const text = parseData.text.toLowerCase();
let intent = 'general';

if (text.match(/fiyat|ucret|ne kadar|kac para|price/i)) {
  intent = 'pricing';
} else if (text.match(/saat|acik|kapali|calisma|when|open|close/i)) {
  intent = 'hours';
} else if (text.match(/adres|nerede|konum|location|where/i)) {
  intent = 'location';
} else if (text.match(/randevu|rezervasyon|appointment|book/i)) {
  intent = 'booking';
} else if (text.match(/kupon|indirim|kampanya|coupon|discount/i)) {
  intent = 'coupon';
} else if (text.match(/merhaba|selam|hey|hi|hello/i)) {
  intent = 'greeting';
} else if (text.match(/tesekkur|sagol|thanks|thank/i)) {
  intent = 'thanks';
}

// Build customer context string for AI
let customerContext = '';
if (isNewCustomer) {
  customerContext = 'Bu musteri ilk kez mesaj atiyor. Sicak karsilama yap.';
} else {
  customerContext = `Geri donen musteri (${interactionCount} onceki etkilesim).`;
  if (couponBalance > 0) {
    customerContext += ` Kupon bakiyesi: ${couponBalance}/4.`;
    if (couponBalance >= 4) {
      customerContext += ' Ucretsiz masaj hakki var!';
    }
  }
}

return [{
  json: {
    senderId: parseData.senderId,
    text: parseData.text,
    messageId: parseData.messageId,
    startTime: parseData.startTime,
    customer: {
      isNew: isNewCustomer,
      interactionCount,
      couponBalance,
      totalEarned,
      phone: customerData.phone
    },
    intent,
    customerContext
  }
}];

const input = $input.first().json;
const enrichedData = $('Enrich Context').first().json;
const startTime = enrichedData.startTime || Date.now();
const responseTime = Date.now() - startTime;

const aiOutput = input.output || input.text || 'Bir hata olustu. Lutfen tekrar deneyin.';

// Clean up response for Instagram (remove markdown, limit length)
let cleanMessage = aiOutput
  .replace(/\*\*/g, '')  // Remove bold markdown
  .replace(/\*/g, '')    // Remove italic markdown
  .replace(/#{1,6}\s/g, '')  // Remove headers
  .substring(0, 1000);   // Instagram message limit

// Detect sentiment from AI response
let sentiment = 'neutral';
if (cleanMessage.match(/tesekkur|memnun|harika|super|guzel/i)) {
  sentiment = 'positive';
} else if (cleanMessage.match(/uzgun|maalesef|sorun|hata/i)) {
  sentiment = 'negative';
}

return [{ 
  json: { 
    senderId: enrichedData.senderId, 
    message: cleanMessage,
    intent: enrichedData.intent,
    sentiment,
    responseTime,
    originalText: enrichedData.text
  } 
}];
